import React, { useState } from 'react';

const UploadPatientData = () => {
  const [jsonData, setJsonData] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [isSuccess, setIsSuccess] = useState(null);

  // Dữ liệu mẫu bạn đã cung cấp
  const data = [
    {
      id: 5,
      symptoms: "Nhìn mờ ở khoảng cách xa, đau đầu khi đọc sách, mỏi mắt khi nhìn màn hình",
      diagnosis: "Cận thị độ 2.5 đi-ốp, loạn thị nhẹ",
      treatment: "Kê đơn kính cận với độ cận -2.5D, độ loạn 0.5D. Tư vấn thực hiện phẫu thuật Lasik"
    },
    {
      id: 6,
      symptoms: "Mắt bị lác trong, khó nhìn tập trung vào một điểm",
      diagnosis: "Lác trong 15 độ, song thị",
      treatment: "Phẫu thuật chỉnh lác, kết hợp tập luyện thị giác"
    },
    {
      id: 7,
      symptoms: "Khó nhìn rõ vật ở gần, hay phải đưa sách ra xa để đọc",
      diagnosis: "Viễn thị độ 1.75 đi-ốp",
      treatment: "Kê đơn kính viễn +1.75D, hướng dẫn vệ sinh mắt"
    },
    // Thêm các dữ liệu khác ở đây
    // ...
  ];

  // Hàm gửi dữ liệu bệnh nhân lên server
  const sendData = async () => {
    setIsLoading(true);
    setIsSuccess(null);

    for (let patient of data) {
      const { symptoms, diagnosis, treatment } = patient;

      try {
        const response = await fetch('http://127.0.0.1:8888/api/viewsAPIHoSo/', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({ symptoms, diagnosis, treatment }),
        });

        if (response.ok) {
          const result = await response.json();
          console.log('Dữ liệu bệnh nhân đã gửi thành công:', result);
        } else {
          throw new Error('Lỗi khi gửi dữ liệu');
        }
      } catch (error) {
        console.error('Lỗi khi gửi dữ liệu:', error);
        setIsSuccess(false);
        setIsLoading(false);
        return; // Dừng ngay nếu có lỗi
      }
    }

    setIsSuccess(true);
    setIsLoading(false);
  };

  return (
    <div>
      <h1>Upload Dữ Liệu Bệnh Nhân</h1>

      {/* Nút gửi dữ liệu */}
      <button onClick={sendData} disabled={isLoading}>
        {isLoading ? 'Đang gửi...' : 'Gửi Dữ Liệu'}
      </button>

      {/* Thông báo thành công hay thất bại */}
      {isSuccess === true && <p>Gửi dữ liệu thành công!</p>}
      {isSuccess === false && <p>Đã xảy ra lỗi khi gửi dữ liệu!</p>}
    </div>
  );
};

export default UploadPatientData;
